package myAssignments.day1;

public class FindOddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=10;
		System.out.print("Odd number from the given range: ");
		for(int i=1; i<=num; i++) {
			if(i%2==1) {
				System.out.print(i+" ");
			}
			
	}

}}
